    const darkTheme = useTheme()
    const toggleTheme = useThemeUpdate()
    const themeStyles={
        backgroundColor: darkTheme? '#333':'#CCC',
        color: darkTheme? '#CCC': '#333',
        padding:'2rem',
        margin:'2rem'
      }

 return (
     <View>
         <Button onPress={toggleTheme} title='toggle theme'/>
     </View>
 )


}

