import Axios from "axios";
import React, { useState, useEffect} from "react";
import {Button, Text, TextInput, View,StatusBar, StyleSheet, FlatList, SafeAreaView, ScrollView } from "react-native";



const FindMyRoommateScreen = (props) => {
  const [loading,setLoading] = useState(true);
  const [query, setQuery] = useState('');
  const [text, setText] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [alert, setAlert] = useState("");
  const [data,setData] = useState([]);

  const APP_ID = "64c826ae";
  const APP_KEY = "e07cf4a17fede26c2683f3da76fc456e";

  const url = `https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`;

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((response) => {
        setData(response)
        console.log(response)
        })
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, [query]);

  const getData = async () => {
    if (query !== "") {
      const result = await Axios.get(url);
      if (!result.data.more) {
        return setAlert("No food with such name");
      }
      console.log(result);
      setRecipes(result.data.hits);
      setQuery("");
      setAlert("");
    } else {
      setAlert("Please fill the form");
    }
  };

  const onChange = e => setQuery(e.target.value);

  const onSubmit = e => {
    e.preventDefault();
    getData();
  };

  const renderItem = ({item}) => {
    return (
      <View style={styles.renderItem}>
        <Text style={{flex:1,textAlign:'center'}}>{item.name.first + " "+ item.name.last}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.gender}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.dob.age}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.location.city +", "+ item.location.state}</Text>  
     </View>
  )}

  const DATA = data.map((x) => {
    x.id = x.login.uuid
    return(x)
  })


  return (
    <SafeAreaView style={styles.container}>

    {/* <View style={{ flex:8}}>
      <View style={{flexDirection:'row'}}>
        <Text style={styles.title}>Name</Text>
        <Text style={styles.title}>Gender</Text>
        <Text style={styles.title}>Age</Text>
        <Text style={styles.title}>Location</Text>
     </View>
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.login.uuid}
      />
    </View> */}

    </SafeAreaView>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
    margin: 15,
    flexDirection:'row',
  },
  renderItem: {
    flex:4,
    backgroundColor: '#FFFFCC',
    padding: 5,
    marginVertical: 8,
    marginHorizontal: 16,
    flexDirection:'row',
  },
  title: {
    fontSize: 20,
    fontStyle:'italic',
    flexDirection:'row',
    flex:1,
    textAlign:'center',
    margin:3
  },
  filter:{
    margin:3,
    fontSize:30,
    flexDirection:'column', 
    justifyContent: 'space-around',

  },

  button: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'black',
  },
});

export default FindMyRoommateScreen;
