import Axios from "axios";
import React, { useState, useEffect} from "react";
import {Button, Text, TextInput, View,StatusBar, StyleSheet, FlatList, SafeAreaView, ScrollView } from "react-native";



const FindMyRoommateScreen = (props) => {
  const [loading,setLoading] = useState(true);
  const [text, setText] = useState('');
  const [data,setData] = useState([]);

  const [query, setQuery] = useState("");
  const [recipes, setRecipes] = useState([]);
  const [alert, setAlert] = useState("");

  const APP_ID = "64c826ae";
  const APP_KEY = "e07cf4a17fede26c2683f3da76fc456e";

  const url = `https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`;

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((response) => {
        setRecipes(response.data);
        setQuery("");
        setAlert("");
        console.log(response)
        })
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, [query]);



  const renderItem = ({item}) => {
    return (
      <View style={styles.renderItem}>
        <Text style={{flex:1,textAlign:'center'}}>{item.recipe.image}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.recipe.label}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.recipe.url}</Text>
        <Text style={{flex:1,textAlign:'center'}}>{item.recipe.ingredients}</Text>  
     </View>
  )}

  const DATA = data.map((x) => {
    x.id = x.login.uuid
    return(x)
  })


  return (
    <SafeAreaView style={styles.container}>

    <View style={{flex:1}}>
      <View style={styles.filter}>
        <Text style={{fontSize:20}}>Filter by    

        <TextInput
          style={{height: 40}}
          placeholder="Food"
          onChangeText={text => {setText(text)}}
        />
        </Text>

        <Button
        onPress={() => {
        setQuery(text)
        }}
        title="Check"
        />
      </View>
    </View>

    <View style={{ flex:8}}>
      {/* <View style={{flexDirection:'row'}}>
        <Text style={styles.title}>Name</Text>
        <Text style={styles.title}>Gender</Text>
        <Text style={styles.title}>Age</Text>
        <Text style={styles.title}>Location</Text>
     </View> */}
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.login.uuid}
      />
    </View>

    </SafeAreaView>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
    margin: 15,
    flexDirection:'row',
  },
  renderItem: {
    flex:4,
    backgroundColor: '#FFFFCC',
    padding: 5,
    marginVertical: 8,
    marginHorizontal: 16,
    flexDirection:'row',
  },
  title: {
    fontSize: 20,
    fontStyle:'italic',
    flexDirection:'row',
    flex:1,
    textAlign:'center',
    margin:3
  },
  filter:{
    margin:3,
    fontSize:30,
    flexDirection:'column', 
    justifyContent: 'space-around',

  },

  button: {
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: 12,
    paddingHorizontal: 32,
    borderRadius: 4,
    elevation: 3,
    backgroundColor: 'black',
  },
});

export default FindMyRoommateScreen;
