import React, { useContext, useEffect, useState } from "react";

import {Button, Text, TextInput, View,StatusBar, StyleSheet, FlatList, SafeAreaView } from "react-native";

import MoodBtns from "./MoodDtns";
import * as API from "../utils/API";
import DeveloperContext from "../utils/DeveloperContext";

const DeveloperInfo = () => {   

    const [developerState, setDeveloperState] = useState({
        mood: "",
        lifeLongLearner: true,
        excitementLevel: 0
      });

      const changeMood=(mood)=> {
        if(mood == "determined") {
          developerState.excitementLevel += 10000;
        }
        else {
          developerState.excitementLevel -= 10000;
        }
        setDeveloperState({
          ...developerState,
          mood
        });
      }

  const { mood, excitementLevel } = useContext(DeveloperContext);

  useEffect(() => {
    // For demonstration purposes, we mock an API call.
    API.getDeveloper.then((res) => {
      setDeveloperState(res);
    });
  }, []);

  return (

    <View >
        <DeveloperContext.Provider value={developerState}>
    <MoodBtns changeMood={changeMood} />
      <View>
        <Text>
            Status: {mood}
        </Text>
        <Text>
            Excitement Level: {excitementLevel}
        </Text> 
      </View>
      </DeveloperContext.Provider>
    </View>

  );
}

export default DeveloperInfo;
